import { CREATE_TODO, CHANGE_TODO_FILTER, CLEAR_COMPLETED_TODO, UPDATE_TODO } from '../constants/todoConstants';
import { fetchTodosFromLocalStorage } from '../utils/todoUtils';

const initialState = {
  items: fetchTodosFromLocalStorage(),
  filter: 'all'
};

const todosReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case CREATE_TODO:
      return {
        ...state,
        items: [...state.items, payload.todo]
      };
    case CHANGE_TODO_FILTER:
      return {
        ...state,
        filter: payload.filter
      };
    case CLEAR_COMPLETED_TODO:
      return {
        ...state,
        items: state.items.filter(({ completed }) => !completed)
      };
    case UPDATE_TODO:
      return {
        ...state,
        items: state.items.map((item) => (item.id === payload.id))
      }
    default:
      return state;
  }
};

export default todosReducer;
